/*  _____ _______         _                      _
 * |_   _|__   __|       | |                    | |
 *   | |    | |_ __   ___| |___      _____  _ __| | __  ___ ____
 *   | |    | | '_ \ / _ \ __\ \ /\ / / _ \| '__| |/ / / __|_  /
 *  _| |_   | | | | |  __/ |_ \ V  V / (_) | |  |   < | (__ / /
 * |_____|  |_|_| |_|\___|\__| \_/\_/ \___/|_|  |_|\_(_)___/___|
 *                                _
 *              ___ ___ ___ _____|_|_ _ _____
 *             | . |  _| -_|     | | | |     |  LICENCE
 *             |  _|_| |___|_|_|_|_|___|_|_|_|
 *             |_|
 *
 *   PROGRAMOVÁNÍ  <>  DESIGN  <>  PRÁCE/PODNIKÁNÍ  <>  HW A SW
 *
 * Tento zdrojový kód je součástí výukových seriálů na
 * IT sociální síti WWW.ITNETWORK.CZ
 *
 * Kód spadá pod licenci prémiového obsahu a vznikl díky podpoře
 * našich členů. Je určen pouze pro osobní užití a nesmí být šířen.
 * Více informací na http://www.itnetwork.cz/licence
 */

import React, {useEffect, useState} from "react";
import {useNavigate, useParams} from "react-router-dom";

import {apiGet, apiPost, apiPut} from "../utils/api";

import InputField from "../components/InputField";
import InputCheck from "../components/InputCheck";
import FlashMessage from "../components/FlashMessage";



const InvoicesForm = () => {
    const navigate = useNavigate();
    const {id} = useParams();
    const [invoices, setInvoice] = useState({
        invoiceNumber: "",
            issued: issued,
            dueDate: dueDate,
            product: product,
            price: price,
            vat: vat,
            note: note
    });

    const [sellerId, setSellerId] = useState(0);
    const [buyerId, setBuyerId] = useState(0);
    const [issued, setIssued] = useState(0);
    const [dueDate, setDueDate] = useState(0);
    const [product, setProduct] = useState("");
    const [price, setPrice] = useState(0);
    const [vat, setVat] = useState(21);
    const [note, setNote] = useState("");

    const [sentState, setSent] = useState(false);
    const [successState, setSuccess] = useState(false);
    const [errorState, setError] = useState(null);

    useEffect(() => {
        if (id) {
            apiGet("/api/invoices/" + id).then((data) => setInvoice(data));
            setInvoiceNumber(data.invoiceNumber);
            sellerId(data.seller._id);
            setBuyerId(data.buyer._id);
            setIssued(data.issued);
            setDueDate(data.dueDate);
            setProduct(data.product);
            setPrice(data.price);
            setVat(data.vat);
            setNote(data.note);
        }
    }, [id]);

    const handleSubmit = (e) => {
        e.preventDefault();

        const invoiceData = {

            seller: {
                _id: sellerId
            },
            buyer: {
                _id: buyerId
            },

        }

        (id ? apiPut("/api/invoices/" + id, invoiceData) : apiPost("/api/invoices", invoiceData))
            .then((data) => {
                setSent(true);
                setSuccess(true);
                navigate("/invoices");
            })
            .catch((error) => {
                console.log(error.message);
                setError(error.message);
                setSent(true);
                setSuccess(false);
            });
    };

    const sent = sentState;
    const success = successState;

    return (
        <div>
            <h1>{id ? "Upravit" : "Vytvořit"} fakturu</h1>
            <hr/>
            {errorState ? (
                <div className="alert alert-danger">{errorState}</div>
            ) : null}
            {sent && (
                <FlashMessage
                    theme={success ? "success" : ""}
                    text={success ? "Uložení faktury proběhlo úspěšně." : ""}
                />
            )}
            <form onSubmit={handleSubmit}>

                <InputField
                    required={true}
                    type="text"
                    name="invoicesNumber"
                    min="3"
                    label="Číslo faktury"
                    prompt="Zadejte číslo faktury"
                    value={invoicesNumber}
                    handleChange={(e) => {
                        setInvoiceNumber({...invoiceData, invoicesNumber: e.target.value});
                    }}
                />

                <InputField
                    required={true}
                    type="number"
                    name="invoicesSeller"
                    min="3"
                    label="Id dodavatele"
                    value={sellerId}
                    handleChange={(e) => {
                        sellerId({...invoiceData.seller, _id: e.target.value});
                    }}
                />

                <InputField
                    required={true}
                    type="number"
                    name="invoicesBuyer"
                    min="3"
                    label="Id odběratele"
                    value={buyerId}
                    handleChange={(e) => {
                        setBuyerId({...invoiceData.buyer, _id: e.target.value});
                    }}
                />

                <InputField
                    required={true}
                    type="text"
                    name="invoicesDueDate"
                    min="3"
                    label="Datum vystavení"
                    value={dueDate}
                    handleChange={(e) => {
                        setDueDate({...invoiceData, dueDate: e.target.value});
                    }}
                />

                <InputField
                    required={true}
                    type="text"
                    name="invoicesIssued"
                    min="3"
                    label="Datum splatnosti"
                    value={issued}
                    handleChange={(e) => {
                        setIssued({...invoiceData, issued: e.target.value});
                    }}
                />

                <InputField
                    required={true}
                    type="text"
                    name="invoicesProduct"
                    min="3"
                    label="Produkt"
                    prompt="Zadejte produkt"
                    value={product}
                    handleChange={(e) => {
                        setProduct({...invoiceData, product: e.target.value});
                    }}
                />

                <InputField
                    required={true}
                    type="number"
                    name="invoicesPrice"
                    min="3"
                    label="cena"
                    prompt="Zadejte cenu"
                    value={price}
                    handleChange={(e) => {
                        setPrice({...invoiceData, price: e.target.value});
                    }}
                />

                <InputField
                    required={true}
                    type="number"
                    name="invoicesVat"
                    min="3"
                    label="Daň"
                    value={vat}
                    handleChange={(e) => {
                        setVat({...invoiceData, vat: e.target.value});
                    }}
                />

                <InputField
                    required={true}
                    type="text"
                    name="invoicesNote"
                    min="3"
                    label="Popis"
                    prompt="Zadejte popis produktu"
                    value={note}
                    handleChange={(e) => {
                        setNote({...invoiceData, note: e.target.value});
                    }}
                />

                <input type="submit" className="btn btn-primary" value="Uložit"/>
            </form>
        </div>
    );
};

export default InvoicesForm;
