import React, {useEffect, useState} from "react";
import {useParams} from "react-router-dom";

import {apiGet} from "../utils/api";

const InvoicePurchases = ({label, items}) => {
    const [purchases, setPurchases] = useState([])

    useEffect(() => {
        apiGet("/api/identification/" + items + "/purchases").then((data) => setPurchases(data));
    }, [items]);

    return(
        <>
            <h1>{label}</h1>
            <hr/>
            <table className="table table-bordered">
                    <thead>
                        <tr>
                            <th>Číslo faktury</th>
                            <th>Odběratel</th>
                            <th>Datum vystavení</th>
                            <th>Datum splatnosti</th>
                            <th>Částka</th>
                        </tr>
                    </thead>
                    <tbody>
                        {purchases.map((item, index) =>(
                           <tr key={index + 1}>
                            <th>{item.invoiceNumber}</th>
                            <th>
                            {invoice.buyer && (
                                <Link to={`/persons/show/${invoice.buyer._id}`} className="link-success" title={`IČ: ${invoice.buyer.identificationNumber}`}>
                                    {invoice.buyer.name}
                    </Link>
                )}
                            </th>
                        </tr> 
                        ))}
                    </tbody>
                </table>
        </>
    )
}

export default InvoicePurchases;
