import InputField from "../components/InputField";
import Select from "react-select"

const InvoicesFilter = ({invoice}) => {

    const options = invoice.map((item) => ({
        value: item.seller._id,
        label: item.seller.name // Předpokládáme, že seller má vlastnost name
    }));

    return (
        <>
            <form>
            <label>
                Dodavatel 
                <select value="" onChange={(e) => console.log(e.target.value)}>
                    <option value="">Vyberte...</option>
                    {invoice.map((item, index) => (
                        <option key={index} value={item.seller._id}>
                            {item.seller._id}
                        </option>
                    ))}
                </select>
            </label>
        </form>
        </>
    )
}

export default InvoicesFilter;
