        value: item.seller._id,
        label: item.seller.name 
    }));

    function handleChange(value){
        setOperation(value.value)
    }

    return (
        <>
            <form>
            <label>
                Dodavatel
                <Select
                    options={options}
                    onChange={handleChange}
                />
            </label>
        </form>
        </>
    )
}

export default InvoicesFilter;
