import {apiGet} from "../utils/api";
import Select from "react-select"
import React, {useEffect, useState} from "react";

const InvoicesFilter = ({onFilterChange }) => {

    const [persons, setPersons] = useState([]);
    const [filterState, setFilter] = useState({
        sellerId: undefined,
        buyerId: undefined,
        maxPrice: undefined,
        minPrice: undefined,
        product: undefined,
        limit: undefined,
      });

    useEffect(() => {
        apiGet("/api/persons").then((data) => setPersons(data));
    }, []);

    const handleFilterChange = (name, value) => {
        setFilter({
            ...filterState,
            [name]: value,
        });

        setPersons({
            value: filterState,
            label: persons.name
        })

        onFilterChange({
            ...filterState,
            [name]: value,
        });
    };

    return (
        <>
            <form>
            <div>
                <div>
                    <label>Dodavatel</label>
                    <Select
                        options={persons.map((person) => ({
                            value: person._id,
                            label: person.name,
                        }))}
                        value={
                            persons.find((person) => person._id === filterState.sellerId) || null
                        }
                        onChange={(selectedOption) =>
                            handleFilterChange("sellerId", selectedOption ? selectedOption.value : "")
                        }
                    />
                </div>
            </div>
        </form>
        </>
    )
}

export default InvoicesFilter;
